(5, 1, '2018-11-08 11:03:30', '2018-11-08 03:03:30', '[et_pb_section fb_built=\"1\" fullwidth=\"on\" _builder_version=\"3.0.47\"][et_pb_fullwidth_post_slider posts_number=\"1\" include_categories=\"3,4\" show_more_button=\"off\" use_text_overlay=\"off\" _builder_version=\"3.17.6\"]\r[/et_pb_fullwidth_post_slider][/et_pb_section][et_pb_section fb_built=\"1\" fullwidth=\"on\" _builder_version=\"3.17.6\"][et_pb_fullwidth_menu menu_id=\"5\" submenu_direction=\"downwards\" fullwidth_menu=\"off\" _builder_version=\"3.17.6\" border_width_bottom=\"2px\" border_color_bottom=\"#c9c9c9\" border_style_bottom=\"dotted\" text_orientation=\"justified\"]\r[/et_pb_fullwidth_menu][/et_pb_section][et_pb_section fb_built=\"1\" _builder_version=\"3.0.47\" custom_padding=\"54px|0px|0|0px|false|false\"][et_pb_row custom_padding=\"26px|0px|27px|0px|false|false\" background_color_1=\"#ffffff\" _builder_version=\"3.17.6\" background_size=\"initial\" background_position=\"top_left\" background_repeat=\"repeat\"][et_pb_column type=\"1_2\" _builder_version=\"3.0.47\" background_color=\"#ffffff\" parallax=\"off\" parallax_method=\"on\"][et_pb_text _builder_version=\"3.17.6\" border_width_all=\"2px\" border_color_all=\"#0c71c3\" border_style_all=\"dashed\" border_color_right=\"#0c71c3\" border_style_right=\"dashed\" custom_padding=\"20px||20px|20px\"]<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>REGION: </strong>Cordillera Administrative Region</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>LOCATION: </strong>Northwest Luzon</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>PROVINCES: </strong>Benguet, Apayao, Abra, Ifugao, Kalinga, Mt. Province</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>POPULATION AS OF 2015: </strong>200,000</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>MAJOR DIALECTS: </strong>Ilocano, Pangalatok, Tagalog</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>MAIN INDUSTRIES:</strong> Coconut</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>MAIN ATTRACTIONS:</strong> Surf, Beach, Windmills, Delicacies</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>HISTORICAL FIGURES:</strong> Marcoses, Aglipay Church</span></p>\r\n<p class=\"font_9\"><span style=\"color: #4c4c4c;\"><strong>MAJOR TRANSPORTS:</strong> PhilTranco, Farinas, DLTB, PAL</span></p>\r[/et_pb_text][/et_pb_column][et_pb_column type=\"1_2\" _builder_version=\"3.0.47\" parallax=\"off\" parallax_method=\"on\"][et_pb_image src=\"http://207.180.199.176/~silippin/wp-content/uploads/2018/11/pbnad.jpg\" align=\"center\" _builder_version=\"3.17.6\" background_color=\"#5170ff\" custom_margin=\"||\" custom_padding=\"40px||40px\" background__hover_enabled=\"on\"]\r[/et_pb_image][/et_pb_column][/et_pb_row][/et_pb_section][et_pb_section fb_built=\"1\" _builder_version=\"3.0.47\" custom_padding=\"0|0px|38px|0px|false|false\"][et_pb_row _builder_version=\"3.0.48\" background_size=\"initial\" background_position=\"top_left\" background_repeat=\"repeat\" custom_padding=\"24px|0px|27px|0px|false|false\"][et_pb_column type=\"1_4\" _builder_version=\"3.0.47\" parallax=\"off\" parallax_method=\"on\"][et_pb_blurb url=\"#\" use_icon=\"on\" font_icon=\"%%154%%\" icon_color=\"#5170ff\" use_circle=\"on\" circle_color=\"#ffffff\" use_circle_border=\"on\" circle_border_color=\"#5170ff\" _builder_version=\"3.17.6\" link_option_url=\"#\"]<h3 style=\"text-align: center;\"><strong>SilipKainan</strong></h3>[/et_pb_blurb][/et_pb_column][et_pb_column type=\"1_4\" _builder_version=\"3.0.47\" parallax=\"off\" parallax_method=\"on\"][et_pb_blurb url=\"#\" use_icon=\"on\" font_icon=\"%%122%%\" icon_color=\"#5170ff\" use_circle=\"on\" circle_color=\"#ffffff\" use_circle_border=\"on\" circle_border_color=\"#5170ff\" _builder_version=\"3.17.6\" link_option_url=\"#\"]<h3 style=\"text-align: center;\"><strong>SilipPasyalan</strong></h3>[/et_pb_blurb][/et_pb_column][et_pb_column type=\"1_4\" _builder_version=\"3.0.47\" parallax=\"off\" parallax_method=\"on\"][et_pb_blurb url=\"#\" use_icon=\"on\" font_icon=\"%%102%%\" icon_color=\"#5170ff\" use_circle=\"on\" circle_color=\"#ffffff\" use_circle_border=\"on\" circle_border_color=\"#5170ff\" _builder_version=\"3.17.6\" link_option_url=\"#\"]<h3 style=\"text-align: center;\"><strong>SilipTulugan</strong></h3>[/et_pb_blurb][/et_pb_column][et_pb_column type=\"1_4\" _builder_version=\"3.0.47\" parallax=\"off\" parallax_method=\"on\"][et_pb_blurb url=\"#\" use_icon=\"on\" font_icon=\"%%121%%\" icon_color=\"#5170ff\" use_circle=\"on\" circle_color=\"#ffffff\" use_circle_border=\"on\" circle_border_color=\"#5170ff\" _builder_version=\"3.17.6\" link_option_url=\"#\"]<h3 style=\"text-align: center;\"><strong>SilipGabay</strong></h3>[/et_pb_blurb][/et_pb_column][/et_pb_row][/et_pb_section]', 'SilipBalita CAR Region', '', 'publish', 'closed', 'closed', '', 'car-region', '', '', '2018-11-14 11:31:22', '2018-11-14 03:31:22', '', 8, 'http://207.180.199.176/~silippin/?page_id=5', 0, 'page', '', 0),
